mapper.select:
INSERT INTO users
(
     user_id, password, type, name, nick_name, email, phone, role, created_at, jibun_addr, road_addr,
     extra_addr, agent_name, agent_number, agent_phone, agent_leader, location_x, location_y
)
VALUES
(
    #{userId}, #{password}, #{type}, #{name}, #{nickName}, #{email}, #{phone}, #{role.name}, current_timestamp, #{jibunAddr}, #{roadAddr},
    #{extraAddr}, #{agentName}, #{agentNumber}, #{agentPhone}, #{agentLeader}, #{locationX}, #{locationY}
)

mapper.update:
UPDATE users
SET password = #{password}, type = #{type}, name = #{name}, nick_name = #{nickName}, email = #{email}, phone = #{phone},
    jibun_addr = #{jibunAddr}, road_addr = #{roadAddr}, extra_addr = #{extraAddr}, agent_name = #{agentName}, agent_number = #{agentNumber},
    agent_phone = #{agentPhone}, agent_leader = #{agentLeader}, location_x = #{locationX}, location_y = #{locationY}
WHERE user_id = #{userId}
